<?php
//===>START READING CONTAINER ENV['UB_APP_ID']
try{
	$UB_APP_ENV = json_decode(getenv('UB_APP_ENV'),TRUE);
}catch(Exception $e){
	$UB_APP_ENV = array();
}
if(isset($UB_APP_ENV['SIMPLESAML_BASE_URL'])){
	$SIMPLESAML_BASE_URL = $UB_APP_ENV['SIMPLESAML_BASE_URL'];
}else{
	$SERVER_PROTO = (!empty($_SERVER['HTTPS']) && $_SERVER['HTTPS'] !== 'off' || $_SERVER['SERVER_PORT'] == 443) ? "https" : "http";
	$SIMPLESAML_BASE_URL = $SERVER_PROTO."://".$_SERVER['HTTP_HOST'];
	$SIMPLESAML_BASE_URL .= str_replace(basename($_SERVER['SCRIPT_NAME']),"",$_SERVER['SCRIPT_NAME']);
}
if(isset($UB_APP_ENV['SIMPLESAML_APP_ID'])){
	$SIMPLESAML_APP_ID = $UB_APP_ENV['SIMPLESAML_APP_ID'];
}else{
	$SIMPLESAML_APP_ID = 'simabafilkom';
}
if(isset($UB_APP_ENV['SIMPLESAML_CERT_DATA'])){
	$SIMPLESAML_CERT_DATA = $UB_APP_ENV['SIMPLESAML_CERT_DATA'];
}else{
	$SIMPLESAML_CERT_DATA = '';
}
//===>END READING CONTAINER ENV['UB_APP_ID']

define('SIMPLESAML_BASE_URL', $SIMPLESAML_BASE_URL);
define('SIMPLESAML_APP_ID', $SIMPLESAML_APP_ID);
define('SIMPLESAML_CERT_DATA', $SIMPLESAML_CERT_DATA);
